startup:
if [[ ! -z ${VERSION} ]]; then
    echo -e "Using version ${VERSION}";
else
    echo -e "Please set the VERSION variable";
    exit 0;
fi;

if [[ -d .git ]] && [[ -z ${MODFILES} ]]; then
    echo -e "Forcing git checkout";
    git fetch --all --tags;
    git checkout tags/${VERSION} -f;
elif [[ -d .git ]] && [[ ! -z ${MODFILES} ]]; then
    echo -e "User modify files enabled";
    git fetch --all --tags;
    git checkout tags/${VERSION};
fi;

if [ -f /home/container/requirements.txt ]; then
    pip install -U --prefix .local -r /home/container/requirements.txt;
fi;

/usr/local/bin/python -m prisma generate --schema mysql-schema.prisma;

/usr/local/bin/python /home/container/launcher.py;
